<?php\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_dbname";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to get all data\n$sql = "SELECT parameter_value FROM user_data";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $sum = 0;\n    $count = 0;\n    $values = [];\n\n    // Calculate mean\n    while($row = $result->fetch_assoc()) {\n        $value = $row['parameter_value'];\n        $sum += $value;\n        $values[] = $value;\n        $count++;\n    }\n\n    $mean = $sum / $count;\n\n    // Calculate standard deviation\n    $sum_of_squares = 0;\n    foreach($values as $value) {\n        $sum_of_squares += pow(($value - $mean), 2);\n    }\n    $std_dev = sqrt($sum_of_squares / $count);\n\n    // Update benchmark values in the database\n    $update_sql = "UPDATE benchmark_table SET mean_value = ?, std_dev = ? WHERE id = 1";\n    $stmt = $conn->prepare($update_sql);\n    $stmt->bind_param("dd", $mean, $std_dev);\n    $stmt->execute();\n    $stmt->close();\n}\n\n$conn->close();\n?>