<?php\nrequire 'vendor/autoload.php';\n\nuse setasign\Fpdi\Fpdi;\n\n// Initialize FPDI\n$pdf = new FPDI();\n\n// Set the source file\n$pageCount = $pdf->setSourceFile('example.pdf');\n\n// Import the first page\n$templateId = $pdf->importPage(1);\n\n// Add a page and use the imported template\n$pdf->addPage();\n$pdf->useTemplate($templateId);\n\n// Set font and position for new text\n$pdf->SetFont('Helvetica', '', 12);\n$pdf->SetXY(50, 50); // Adjust the coordinates as needed\n\n// Add new text\n$newText = 'This is the new text';\n$pdf->Write(0, $newText);\n\n// Output the new PDF\n$pdf->Output('F', 'modified_example.pdf');\n?>