<?php\n   $dsn = 'IBM_DB2';\n   $user = 'YOUR_USERNAME';\n   $password = 'YOUR_PASSWORD';\n\n   $conn = odbc_connect($dsn, $user, $password);\n\n   if ($conn) {\n       $sql = "SELECT * FROM your_table";\n       $result = odbc_exec($conn, $sql);\n\n       while ($row = odbc_fetch_array($result)) {\n           print_r($row);\n       }\n\n       odbc_close($conn);\n   } else {\n       echo "Connection failed: " . odbc_errormsg();\n   }\n   ?>