<?php\n// Original date string in the format YYYY-DD-MM\n$dateString = '2023-15-10';\n\n// Convert the date string to the format YYYY-MM-DD\n$date = DateTime::createFromFormat('Y-d-m', $dateString);\n$formattedDate = $date->format('Y-m-d');\n\n// Now you can use $formattedDate in your MySQL query\n$query = "SELECT * FROM your_table WHERE your_timestamp_column = '$formattedDate'";\n?>