<?php\n\nfunction show()\n{\n    $a = mysql_fetch_array(mysql_query('SELECT DATABASE()'));\n    echo $a[0] . "<br>";\n}\n\n// Connect to the first database\n$conn1 = mysql_connect('localhost', 'root', '', TRUE);\nmysql_select_db('dredd', $conn1);\nshow();\n\n// Connect to the second database\n$conn2 = mysql_connect('localhost', 'root', '', TRUE);\nmysql_select_db('afup', $conn2);\nshow();\n\n// Switch back to the first connection\nmysql_select_db('dredd', $conn1);\nshow();\n\n// Switch to the second connection\nmysql_select_db('afup', $conn2);\nshow();\n\nmysql_close($conn1);\nmysql_close($conn2);\n?>