$image = imagecreatefromgif($source_file);\n$watermark = imagecreatefrompng('path_to_your_watermark.png'); // Assuming watermark is in PNG format\n\n// Get dimensions of the source image and watermark\nlist($source_width, $source_height) = getimagesize($source_file);\nlist($water_width, $water_height) = getimagesize('path_to_your_watermark.png');\n\n// Set the position of the watermark\n$x = ($source_width - $water_width) / 2;\n$y = ($source_height - $water_height) / 2;\n\n// Apply the watermark\nimagecopy($image, $watermark, $x, $y, 0, 0, $water_width, $water_height);\n\n// Save the result\nimagegif($image, $source_file);\n\n// Free up memory\nimagedestroy($image);\nimagedestroy($watermark);