<?php\n$accessLicenseNumber = 'YOUR_ACCESS_LICENSE_NUMBER';\n$userId = 'YOUR_USER_ID';\n$password = 'YOUR_PASSWORD';\n$shipperNumber = 'YOUR_SHIPPER_NUMBER';\n\n$endpoint = 'https://onlinetools.ups.com/ups.app/xml/ShipConfirm';\n\n// Prepare the XML request\n$requestXML = <<<XML\n<?xml version="1.0"?>\n<AccessRequest xml:lang="en-US">\n    <AccessLicenseNumber>$accessLicenseNumber</AccessLicenseNumber>\n    <UserId>$userId</UserId>\n    <Password>$password</Password>\n</AccessRequest>\n<?xml version="1.0"?>\n<ShipmentConfirmRequest>\n    <Request>\n        <RequestAction>ShipConfirm</RequestAction>\n        <RequestOption>nonvalidate</RequestOption>\n    </Request>\n    <Shipment>\n        <Shipper>\n            <Name>Your Company Name</Name>\n            <ShipperNumber>$shipperNumber</ShipperNumber>\n            <Address>\n                <AddressLine1>123 Shipper St</AddressLine1>\n                <City>Shipper City</City>\n                <StateProvinceCode>CA</StateProvinceCode>\n                <PostalCode>12345</PostalCode>\n                <CountryCode>US</CountryCode>\n            </Address>\n        </Shipper>\n        <ShipTo>\n            <CompanyName>Recipient Company</CompanyName>\n            <AttentionName>John Doe</AttentionName>\n            <PhoneNumber>1234567890</PhoneNumber>\n            <Address>\n                <AddressLine1>456 Recipient Ave</AddressLine1>\n                <City>Recipient City</City>\n                <StateProvinceCode>NY</StateProvinceCode>\n                <PostalCode>67890</PostalCode>\n                <CountryCode>US</CountryCode>\n            </Address>\n        </ShipTo>\n        <Service>\n            <Code>03</Code>\n            <Description>Ground</Description>\n        </Service>\n        <Package>\n            <PackagingType>\n                <Code>02</Code>\n                <Description>Package</Description>\n            </PackagingType>\n            <PackageWeight>\n                <UnitOfMeasurement>\n                    <Code>LBS</Code>\n                </UnitOfMeasurement>\n                <Weight>10</Weight>\n            </PackageWeight>\n        </Package>\n    </Shipment>\n</ShipmentConfirmRequest>\nXML;\n\n// Initialize cURL\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $endpoint);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $requestXML);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: application/xml'));\n\n// Execute the request and get response\n$response = curl_exec($ch);\nif (curl_errno($ch)) {\n    echo 'Error: ' . curl_error($ch);\n} else {\n    echo 'Response: ' . htmlentities($response);\n}\ncurl_close($ch);\n?>