$start_hours = filter_input(INPUT_GET, 'start_hours', FILTER_SANITIZE_NUMBER_INT);\n$start_minutes = filter_input(INPUT_GET, 'start_minutes', FILTER_SANITIZE_NUMBER_INT);\n$start_ampm = filter_input(INPUT_GET, 'start_ampm', FILTER_SANITIZE_STRING);\n\nif ($start_hours !== null && $start_minutes !== null && ($start_ampm === 'AM' || $start_ampm === 'PM')) {\n    $start_date = date('Y-m-d H:i:s', strtotime("{$start_hours}:{$start_minutes} {$start_ampm}"));\n\n    $date = new DateTime($start_date);\n    $date->modify('+1 day');\n    $new_date = $date->format('Y-m-d H:i:s');\n\n    echo $new_date;\n} else {\n    echo "Invalid input.";\n}