// src/Product.php\n    use Doctrine\ORM\Mapping as ORM;\n\n    /**\n     * @ORM\Entity\n     * @ORM\Table(name="products")\n     **/\n    class Product\n    {\n        /** \n         * @ORM\Id\n         * @ORM\Column(type="integer")\n         * @ORM\GeneratedValue\n         **/\n        private $id;\n\n        /** \n         * @ORM\Column(type="string") \n         **/\n        private $name;\n\n        public function getId()\n        {\n            return $this->id;\n        }\n\n        public function getName()\n        {\n            return $this->name;\n        }\n\n        public function setName($name)\n        {\n            $this->name = $name;\n        }\n    }