<?php\nfunction fetchRssFeeds($url) {\n    $content = @file_get_contents($url);\n    \n    if ($content === FALSE) {\n        return "Could not access the URL.";\n    }\n\n    // Check if the URL is a valid RSS feed\n    if (simplexml_load_string($content)) {\n        return "The URL is a valid RSS feed.";\n    }\n\n    // If not, look for RSS feed links in the HTML content\n    $doc = new DOMDocument();\n    @$doc->loadHTML($content);\n    $xpath = new DOMXPath($doc);\n    \n    $feeds = [];\n    foreach ($xpath->query("//link[@type='application/rss+xml']") as $node) {\n        $feeds[] = $node->getAttribute("href");\n    }\n    \n    if (empty($feeds)) {\n        return "No RSS feeds found.";\n    } else {\n        return $feeds;\n    }\n}\n\n// Example usage\n$url = "http://techcrunch.com";\n$result = fetchRssFeeds($url);\nprint_r($result);\n?>