<?php\nfunction containsForbiddenSubstring($username, $forbiddenSubstring) {\n    // Use strpos to check if the forbidden substring exists in the username\n    if (strpos($username, $forbiddenSubstring) !== false) {\n        return true;\n    }\n    return false;\n}\n\n$username = "Game_Admin";\n$forbiddenSubstring = "Admin";\n\nif (containsForbiddenSubstring($username, $forbiddenSubstring)) {\n    echo "The username contains a forbidden substring.";\n} else {\n    echo "The username is acceptable.";\n}\n?>