<?php\nclass A {\n    public $publicVariable = 'Hello from A';\n\n    public function __construct() {\n        $foo = new B($this); // Passing the instance of A to B\n        $foo->showParentVariable();\n    }\n}\n\nclass B {\n    private $parent;\n\n    public function __construct($parent) {\n        $this->parent = $parent;\n    }\n\n    public function showParentVariable() {\n        echo $this->parent->publicVariable;\n    }\n}\n\n$a = new A();\n?>