<?php\nrequire 'path/to/PHPMailerAutoload.php';\n\n// Initialize PHPMailer\n$mail = new PHPMailer;\n\n// Your data URI\n$contact_image_data = "data:image/png;base64,iVBORw0KGgo[...]";\n$filename = "test.png";\n$encoding = "base64";\n$type = "image/png";\n\n// Extract base64 data from the data URI\nlist($type, $data) = explode(';', $contact_image_data);\nlist(, $data) = explode(',', $data);\n$decoded_data = base64_decode($data);\n\n// Add the decoded data as an attachment\n$mail->AddStringAttachment($decoded_data, $filename, $encoding, $type);\n\n// Set up the rest of your email\n$mail->setFrom('from@example.com', 'Mailer');\n$mail->addAddress('recipient@example.com', 'Recipient');\n$mail->Subject = 'Here is your image';\n$mail->Body    = 'Please find the attached image.';\n\n// Send the email\nif(!$mail->send()) {\n    echo 'Message could not be sent.';\n    echo 'Mailer Error: ' . $mail->ErrorInfo;\n} else {\n    echo 'Message has been sent';\n}\n?>