<?php\nfunction myFunction($mandatoryParam, $optionalParam1 = 'default value', $optionalParam2 = 100) {\n    // Function body here\n    echo "Mandatory: $mandatoryParam\n";\n    echo "Optional 1: $optionalParam1\n";\n    echo "Optional 2: $optionalParam2\n";\n}\n\n// Calling the function with all parameters\nmyFunction('required', 'optional value', 200);\n\n// Calling the function with only the mandatory parameter\nmyFunction('required');\n?>