function saveXml($items, $fileName, $projectName) {\n    // Create a new DOMDocument instance\n    $doc = new DOMDocument('1.0', 'UTF-8');\n    $doc->formatOutput = true;\n\n    // Create the root RSS element\n    $rss = $doc->createElement('rss');\n    $rss->setAttribute('version', '2.0');\n    $rss->setAttribute('xmlns:dc', 'http://purl.org/dc/elements/1.1/');\n    $doc->appendChild($rss);\n\n    // Create the channel element\n    $channel = $doc->createElement('channel');\n    $rss->appendChild($channel);\n\n    // Add channel elements\n    $title = $doc->createElement('title', $projectName);\n    $channel->appendChild($title);\n\n    $link = $doc->createElement('link', 'http://www.mywebsite.com');\n    $channel->appendChild($link);\n\n    $description = $doc->createElement('description', 'My description');\n    $channel->appendChild($description);\n\n    $lastBuildDate = $doc->createElement('lastBuildDate', date(DATE_RSS));\n    $channel->appendChild($lastBuildDate);\n\n    // Add items to the channel\n    foreach ($items as $itemData) {\n        $item = $doc->createElement('item');\n        $channel->appendChild($item);\n\n        $itemTitle = $doc->createElement('title', htmlspecialchars($itemData['title']));\n        $item->appendChild($itemTitle);\n\n        $pubDate = $doc->createElement('pubDate', date(DATE_RSS, strtotime($itemData['pubDate'])));\n        $item->appendChild($pubDate);\n\n        $creator = $doc->createElement('dc:creator', htmlspecialchars($itemData['creator']));\n        $item->appendChild($creator);\n\n        $description = $doc->createElement('description');\n        $description->appendChild($doc->createCDATASection($itemData['description']));\n        $item->appendChild($description);\n\n        $link = $doc->createElement('link');\n        $link->appendChild($doc->createCDATASection($itemData['link']));\n        $item->appendChild($link);\n\n        $guid = $doc->createElement('guid', htmlspecialchars($itemData['link']));\n        $item->appendChild($guid);\n\n        $category = $doc->createElement('category', 'Mycategory');\n        $item->appendChild($category);\n    }\n\n    // Save the XML to a file\n    $doc->save($fileName);\n}\n\n// Example usage\n$items = [\n    [\n        'title' => 'First Post',\n        'pubDate' => '2023-10-10',\n        'creator' => 'Author Name',\n        'description' => 'This is the first post.',\n        'link' => 'http://www.mywebsite.com/first-post'\n    ],\n    // Add more items as needed\n];\n\nsaveXml($items, 'rss.xml', 'My Project');