function transliterate($text) {\n    // Convert the string to ASCII\n    $ascii = iconv('UTF-8', 'ASCII//TRANSLIT//IGNORE', $text);\n    // Replace non-alphanumeric characters with hyphens\n    $ascii = preg_replace('/[^A-Za-z0-9-]+/', '-', $ascii);\n    // Convert to lowercase and trim any leading or trailing hyphens\n    $ascii = strtolower(trim($ascii, '-'));\n    \n    return $ascii;\n}\n\n// Example usage\n$originalText = "Héllo Wörld! Let's make URLs great again.";\n$transliteratedText = transliterate($originalText);\necho $transliteratedText; // Output: hello-world-let-s-make-urls-great-agai