<?php\n$servername = "localhost";\n$username = "root";\n$password = "";\n$dbname = "your_database";\n$table = "your_table";\n$numberOfRows = 10;\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "DESCRIBE $table";\n$result = $conn->query($sql);\n\nif (!$result) {\n    die("Error retrieving table structure: " . $conn->error);\n}\n\n$columns = [];\nwhile ($row = $result->fetch_assoc()) {\n    $columns[] = $row;\n}\n\nfunction generateRandomData($type) {\n    switch ($type) {\n        case strpos($type, 'int') !== false:\n            return rand(1, 100);\n        case strpos($type, 'varchar') !== false:\n            return substr(str_shuffle("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"), 0, 10);\n        case strpos($type, 'text') !== false:\n            return substr(str_shuffle("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ "), 0, 50);\n        case strpos($type, 'date') !== false:\n            return date('Y-m-d', rand(strtotime('-1 year'), strtotime('+1 year')));\n        default:\n            return '';\n    }\n}\n\n$insertSql = "INSERT INTO $table (";\n$insertSql .= implode(", ", array_column($columns, 'Field'));\n$insertSql .= ") VALUES ";\n\n$values = [];\nfor ($i = 0; $i < $numberOfRows; $i++) {\n    $rowValues = [];\n    foreach ($columns as $column) {\n        $rowValues[] = "'" . generateRandomData($column['Type']) . "'";\n    }\n    $values[] = "(" . implode(", ", $rowValues) . ")";\n}\n\n$insertSql .= implode(", ", $values);\n\nif ($conn->query($insertSql) === TRUE) {\n    echo "New records created successfully";\n} else {\n    echo "Error: " . $insertSql . "<br>" . $conn->error;\n}\n\n$conn->close();\n?>