$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Select the event end date of event ID\n$sql = "SELECT enddate FROM mm_eventlist_dates WHERE id = ?";\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("i", $id);\n$stmt->execute();\n$stmt->bind_result($enddate);\n$stmt->fetch();\n$stmt->close();\n\n// Plug in the event end date, find event that starts the next day\n$sql = "SELECT id FROM mm_eventlist_dates WHERE startdate = DATE_ADD(?, INTERVAL 1 DAY)";\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("s", $enddate);\n$stmt->execute();\n$stmt->bind_result($nextEventId);\n$stmt->fetch();\n$stmt->close();\n\necho "Next Event ID: " . $nextEventId;\n\n$mysqli->close();