$array1 = array(\n    array(\n        'id' => 1,\n        'name' => 'Cat',\n    ),\n    array(\n        'id' => 2,\n        'name' => 'Mouse',\n    )\n);\n\n$array2 = array(\n    array(\n        'id' => 2,\n        'age' => 321,\n    ),\n    array(\n        'id' => 1,\n        'age' => 123,\n    )\n);\n\n// Convert first array to associative array using 'id' as the key\n$merged = array();\nforeach ($array1 as $item) {\n    $merged[$item['id']] = $item;\n}\n\n// Merge second array into the associative array\nforeach ($array2 as $item) {\n    if (isset($merged[$item['id']])) {\n        $merged[$item['id']] = array_merge($merged[$item['id']], $item);\n    } else {\n        $merged[$item['id']] = $item;\n    }\n}\n\n// Reset keys to have a numerically indexed array\n$merged = array_values($merged);\n\nprint_r($merged);