<?php\n// Assume $username and $password are the values submitted by the user\n$username = $_POST['username'];\n$password = $_POST['password'];\n\n// Securely connect to the database\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Use prepared statements to avoid SQL injection\n$stmt = $mysqli->prepare("SELECT username, password FROM users WHERE BINARY username = ?");\n$stmt->bind_param("s", $username);\n$stmt->execute();\n$result = $stmt->get_result();\n\nif ($result->num_rows === 1) {\n    $row = $result->fetch_assoc();\n    // Verify the password (assuming it's stored securely, e.g., hashed)\n    if (password_verify($password, $row['password'])) {\n        // Successful login\n        echo "Login successful";\n    } else {\n        // Invalid password\n        echo "Incorrect username or password";\n    }\n} else {\n    // Invalid username\n    echo "Incorrect username or password";\n}\n\n$stmt->close();\n$mysqli->close();\n?>