<?php\nfunction removeFirstTwoWords($string) {\n    $words = explode(' ', $string);\n    $remainingWords = array_slice($words, 2);\n    return implode(' ', $remainingWords);\n}\n\n// Example usage\n$string1 = "R 124 This is my message";\n$string2 = "R 1345255 This is another message";\n\necho removeFirstTwoWords($string1); // Output: "This is my message"\necho "\n";\necho removeFirstTwoWords($string2); // Output: "This is another message"\n?>