$xml = '<test>\n    <element>\n        <bla1>x</bla1>\n        <bla2>x</bla2>\n    </element>\n    <element>\n        <bla1>y</bla1>\n        <bla2>y</bla2>\n    </element>\n    <element>\n        <bla1>z</bla1>\n        <bla2>z</bla2>\n    </element>\n</test>';\n\n$doc = new DOMDocument();\n$doc->loadXML($xml);\n\n$documentElement = $doc->documentElement; // This is the <test> element\n$rootNode = $documentElement->parentNode; // This is the root node\n\nif ($documentElement->hasChildNodes()) {\n    echo 'yayy!';\n}\n\n// Example of other operations on the root node\necho $rootNode->nodeName; // Output: #document