<?php\n// Database connection\n$dbconn = pg_connect("host=your_host dbname=your_db user=your_user password=your_password");\n\n// Prepare a query to select the hashed password for the given email\n$query = "SELECT passhash_md5 FROM users WHERE email=$1";\n$result = pg_prepare($dbconn, "login_query", $query);\n\n// Execute the prepared statement with the provided email\n$email = $_POST['email']; // Assuming the email is posted from a form\n$result = pg_execute($dbconn, "login_query", array($email));\n\nif ($result) {\n    $row = pg_fetch_assoc($result);\n    if ($row && password_verify($_POST['password'], $row['passhash_md5'])) {\n        // Password is correct, proceed with login\n        echo "Login successful!";\n    } else {\n        // Invalid email or password\n        echo "Invalid email or password.";\n    }\n} else {\n    // Query execution failed\n    echo "An error occurred.";\n}\n\n// Close the database connection\npg_close($dbconn);\n?>