<?php\n\nfunction get_php_files($dir) {\n    $rii = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($dir));\n    $files = [];\n\n    foreach ($rii as $file) {\n        if (!$file->isDir() && $file->getExtension() === 'php') {\n            $files[] = $file->getPathname();\n        }\n    }\n\n    return $files;\n}\n\nfunction get_defined_functions_from_files($files) {\n    $defined_functions = [];\n\n    foreach ($files as $file) {\n        $content = file_get_contents($file);\n        $tokens = token_get_all($content);\n\n        for ($i = 0; $i < count($tokens); $i++) {\n            if ($tokens[$i][0] === T_FUNCTION && $tokens[$i + 2][0] === T_STRING) {\n                $defined_functions[] = $tokens[$i + 2][1];\n            }\n        }\n    }\n\n    return $defined_functions;\n}\n\nfunction find_function_usages($files, $function_name) {\n    foreach ($files as $file) {\n        $content = file_get_contents($file);\n        if (strpos($content, $function_name) !== false) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\n$dir = '/path/to/your/php/project';\n$files = get_php_files($dir);\n$defined_functions = get_defined_functions_from_files($files);\n\n$unused_functions = [];\nforeach ($defined_functions as $function) {\n    if (!find_function_usages($files, $function)) {\n        $unused_functions[] = $function;\n    }\n}\n\necho "Unused functions:\n";\nforeach ($unused_functions as $function) {\n    echo $function . "\n";\n}