<?php\n$bearer_token = 'YOUR_BEARER_TOKEN'; // Replace with your actual bearer token from Twitter Developer account\n$username = 'twitter'; // Twitter username\n\n// Set up the cURL request\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, "https://api.twitter.com/2/users/by/username/$username");\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, [\n    "Authorization: Bearer $bearer_token"\n]);\n\n// Execute the request and fetch response\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Decode JSON response\n$user_data = json_decode($response, true);\n\n// Get profile image URL\nif (isset($user_data['data']['profile_image_url'])) {\n    echo $user_data['data']['profile_image_url'];\n} else {\n    echo "Profile image URL not found.";\n}\n?>