$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Fetch categories\n$categories = $mysqli->query("SELECT id, name FROM forumcats");\n\nwhile ($cat = $categories->fetch_assoc()) {\n    echo "<h1>" . htmlspecialchars($cat['name']) . "</h1>";\n\n    // Fetch forums for each category\n    $cat_id = $cat['id'];\n    $forums = $mysqli->query("SELECT name FROM forums WHERE cat_id = $cat_id");\n\n    while ($forum = $forums->fetch_assoc()) {\n        echo "<h3>" . htmlspecialchars($forum['name']) . "</h3>";\n    }\n}\n\n// Close connection\n$mysqli->close();