<?php\nsession_start();\n\nif (isset($_GET['destroy']) && $_GET['destroy'] == 1) {\n    session_destroy();\n    header("Location: " . $_SERVER['PHP_SELF']); // Redirect to clear the URL parameter\n    exit;\n} else {\n    if (!isset($_SESSION['counter'])) {\n        $_SESSION['counter'] = 0;\n    }\n    $_SESSION['counter']++;\n}\n?>\n\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Session test</title>\n</head>\n<body>\n    <?php\n    if (isset($_GET['destroy']) && $_GET['destroy'] == 1) {\n        echo "Session destroyed.<br>";\n    }\n    ?>\n    \n    You have visited this page <?php echo $_SESSION['counter']; ?> times.<br>\n    I am tracking you using the session id <?php echo session_id(); ?>.<br>\n    Click <a href="<?php echo $_SERVER['PHP_SELF']; ?>?destroy=1">here</a> to destroy the session.\n</body>\n</html>