// Function to get previous or next tweet\nfunction getAdjacentTweet($key, $direction) {\n    // Connect to your database\n    $conn = new mysqli("your_host", "your_username", "your_password", "your_database");\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Get the current tweet information\n    $sql = "SELECT tweet_id FROM tweets WHERE tweet_key = ? LIMIT 1";\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param("s", $key);\n    $stmt->execute();\n    $result = $stmt->get_result()->fetch_assoc();\n\n    if (!$result) {\n        return null;\n    }\n\n    $tweet_id = $result['tweet_id'];\n\n    if ($direction == 'next') {\n        $sql = "SELECT * FROM tweets WHERE tweet_id > ? ORDER BY tweet_id ASC LIMIT 1";\n    } else {\n        $sql = "SELECT * FROM tweets WHERE tweet_id < ? ORDER BY tweet_id DESC LIMIT 1";\n    }\n\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param("i", $tweet_id);\n    $stmt->execute();\n    $result = $stmt->get_result()->fetch_assoc();\n\n    $stmt->close();\n    $conn->close();\n\n    return $result;\n}