// plugins/ExamplePlugin.php\nclass ExamplePlugin {\n    public function __construct() {\n        EventManager::listen('onUserLogin', [$this, 'handleUserLogin']);\n    }\n\n    public function handleUserLogin($user) {\n        // Example plugin logic: log user login\n        file_put_contents('user_logins.log', $user->username . ' logged in at ' . date('Y-m-d H:i:s') . PHP_EOL, FILE_APPEND);\n    }\n}\n\nnew ExamplePlugin();