<?php\n// Array of IDs\n$list = [1, 2, 3, 4, 5]; // example IDs\n\n// Create a string of placeholders for the prepared statement\n$placeholders = implode(',', array_fill(0, count($list), '?'));\n\n// Construct the query\n$query = "UPDATE foo SET flag=true WHERE id IN ($placeholders)";\n\n// Prepare the statement\n$stmt = $pdo->prepare($query);\n\n// Execute the statement with the list of IDs as parameters\n$stmt->execute($list);\n?>