function updateTransactionStatusSOAP($uniqueReference, $amount, $status, $statusDescription) {\n    $wsdl = "https://example.com/api/transaction?wsdl"; // Replace with the actual WSDL URL\n\n    try {\n        $client = new SoapClient($wsdl);\n        $params = array(\n            'uniqueReference' => $uniqueReference,\n            'amount' => $amount,\n            'status' => $status,\n            'statusDescription' => $statusDescription\n        );\n\n        $response = $client->__soapCall("UpdateTransactionStatus", array($params));\n        return $response;\n    } catch (SoapFault $fault) {\n        // Handle exception accordingly\n        return "Error: {$fault->faultcode}, {$fault->faultstring}";\n    }\n}\n\n// Example usage\n$response = updateTransactionStatusSOAP('12345', 100.00, 'Completed', 'Transaction completed successfully');\nprint_r($response);