function updateTransactionStatus($uniqueReference, $amount, $status, $statusDescription) {\n    $url = "https://example.com/api/transaction/update"; // Replace with the actual URL of the web service\n    $data = array(\n        'unique_reference' => $uniqueReference,\n        'amount' => $amount,\n        'status' => $status,\n        'status_description' => $statusDescription\n    );\n\n    $options = array(\n        CURLOPT_URL => $url,\n        CURLOPT_POST => true,\n        CURLOPT_POSTFIELDS => http_build_query($data),\n        CURLOPT_RETURNTRANSFER => true,\n        CURLOPT_HTTPHEADER => array('Content-Type: application/x-www-form-urlencoded')\n    );\n\n    $ch = curl_init();\n    curl_setopt_array($ch, $options);\n    $response = curl_exec($ch);\n    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n\n    if(curl_errno($ch)) {\n        $error_msg = curl_error($ch);\n        // Handle error accordingly\n    }\n    curl_close($ch);\n\n    return array('response' => $response, 'http_code' => $httpCode);\n}\n\n// Example usage\n$response = updateTransactionStatus('12345', 100.00, 'Completed', 'Transaction completed successfully');\nprint_r($response);