<?php\n\nclass Person\n{\n    public $firstName;\n    public $lastName;\n    public $age;\n\n    public function __construct($firstName, $lastName, $age)\n    {\n        $this->firstName = $firstName;\n        $this->lastName = $lastName;\n        $this->age = $age;\n    }\n\n    public function introduce()\n    {\n        echo "Hello, my name is {$this->firstName} {$this->lastName} and I am {$this->age} years old.";\n    }\n}\n\n// Example usage:\n$person = new Person("John", "Doe", 30);\n$person->introduce();\n?>