<?php\ndefine('APPLICATION_PATH', realpath(dirname(__FILE__) . '/../application'));\n\n// Set include path to the library\nset_include_path(implode(PATH_SEPARATOR, array(\n    realpath(APPLICATION_PATH . '/../library'),\n    get_include_path(),\n)));\n\n// Include the Zend Framework core\nrequire_once 'Zend/Application.php';\n\n// Create application, bootstrap, and run\n$application = new Zend_Application(\n    'development',\n    APPLICATION_PATH . '/configs/application.ini'\n);\n\n$requestUri = $_SERVER['REQUEST_URI'];\n$queryString = $_SERVER['QUERY_STRING'];\n\n// Remove query string from the request URI\nif (!empty($queryString)) {\n    $requestUri = str_replace('?' . $queryString, '', $requestUri);\n}\n\n// Route the request\n$request = new Zend_Controller_Request_Http();\n$request->setRequestUri($requestUri);\n\n// Setup the front controller\n$frontController = Zend_Controller_Front::getInstance();\n$frontController->setRequest($request);\n$frontController->setBaseUrl('/index.php');\n\n// Dispatch the request\n$application->bootstrap()->run();