<?php\n   require 'vendor/autoload.php';\n\n   use GuzzleHttp\Client;\n   use Symfony\Component\DomCrawler\Crawler;\n\n   $html = '<div>This is some <b>text';\n\n   // Load the HTML\n   $crawler = new Crawler($html);\n\n   // Correcting malformed HTML\n   $correctedHtml = $crawler->html();\n\n   // Manipulate the DOM\n   $crawler->filter('div')->each(function (Crawler $node) {\n       $node->getNode(0)->nodeValue .= " with added content";\n   });\n\n   // Output the modified HTML\n   echo $crawler->html();\n   ?>