$string = "asfasdfasdfasdfasdf[[sometextomatch]]asfkjasdfjaskldfj";\n\n// Non-greedy match\n$regex1 = "/\[\[(.+?)\]\]/";\npreg_match($regex1, $string, $matches1);\necho "Non-greedy match: " . $matches1[1] . "\n";  // Outputs: sometextomatch\n\n// Greedy match\n$regex2 = "/\[\[(.*)\]\]/";\npreg_match($regex2, $string, $matches2);\necho "Greedy match: " . $matches2[1] . "\n";  // Outputs: sometextomatch