$request = trim(file_get_contents('test.xml'));\n$curlHandle = curl_init($servletURL);\ncurl_setopt($curlHandle, CURLOPT_POST, TRUE);\ncurl_setopt($curlHandle, CURLOPT_POSTFIELDS, $request);\ncurl_setopt($curlHandle, CURLOPT_RETURNTRANSFER, TRUE);\ncurl_setopt($curlHandle, CURLOPT_HEADER, FALSE);\ncurl_setopt($curlHandle, CURLOPT_HTTPHEADER, array('Content-Type: text/xml'));  // Explicitly set content-type to text/xml\n$response = curl_exec($curlHandle);\ncurl_close($curlHandle);\n\nif ($response === FALSE) {\n    echo "cURL Error: " . curl_error($curlHandle);\n} else {\n    echo "Response: " . $response;\n}