<?php\n$filePath = 'C:\\path\\to\\your\\file.txt';\n\nif (file_exists($filePath)) {\n    $shell = new COM('Shell.Application');\n    $recycleBin = $shell->NameSpace(10); // 10 is the namespace ID for the Recycle Bin\n\n    if ($recycleBin) {\n        $file = $shell->NameSpace(dirname($filePath))->ParseName(basename($filePath));\n        $file->InvokeVerb("delete");\n        echo "File moved to Recycle Bin.";\n    } else {\n        echo "Failed to access Recycle Bin.";\n    }\n} else {\n    echo "File does not exist.";\n}\n?>