// Sample string with backslashes\n$originalString = "This is a string with \\ backslashes and 'single quotes'.";\n\n// Add slashes for database insertion\n$escapedString = addslashes($originalString);\n\n// Insert into database\n// Your database insertion code here\n\n// Retrieve from database\n// Your database retrieval code here\n\n// Remove slashes added by addslashes, but retain the original backslashes\n$unescapedString = stripslashes($escapedString);\n\n// Replace escaped backslashes with a temporary unique placeholder\n$placeholder = 'TEMP_BACKSLASH_PLACEHOLDER';\n$withPlaceholder = str_replace('\\\\', $placeholder, $unescapedString);\n\n// Remove slashes added by addslashes\n$withStripslashes = stripslashes($withPlaceholder);\n\n// Revert the temporary placeholder back to original backslashes\n$finalString = str_replace($placeholder, '\\', $withStripslashes);\n\necho $finalString;