<?php\n    $url = 'http://yourdomain.com/mod_rewrite_test'; // Make sure to replace with your actual domain\n\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    if (strpos($response, 'mod_rewrite is enabled') !== false) {\n        echo "mod_rewrite is enabled";\n    } else {\n        echo "mod_rewrite is not enabled";\n    }\n    ?>