<?php\n$xmlString = '\n<professions>\n    <skill key="IT Specialist" maxage="40" group="IT" worked="5"/>\n    <skill key="Assistant" maxage="35" group="Office" worked="5"/>\n</professions>\n';\n\n// Load the XML string into a SimpleXMLElement object\n$xml = simplexml_load_string($xmlString);\n\n// Check if the XML was loaded successfully\nif ($xml === false) {\n    echo "Failed to load XML\n";\n    foreach(libxml_get_errors() as $error) {\n        echo "\t", $error->message;\n    }\n    exit;\n}\n\n// Iterate over each skill element and extract the attributes\nforeach ($xml->skill as $skill) {\n    $key = (string) $skill['key'];\n    $maxage = (int) $skill['maxage'];\n    $group = (string) $skill['group'];\n    $worked = (int) $skill['worked'];\n\n    // Do something with the extracted data\n    echo "Skill: $key, Max Age: $maxage, Group: $group, Worked: $worked\n";\n}\n?>