$pattern = '/\n    ^J[0-9]{7}:\s+          # Match and ignore the first part\n    (.*?)                   # Capture the Transaction Start Date\n    \s+J[0-9]{7}:\s+Project\sname:\s+\n    (.*?)                   # Capture the Project Name\n    \s+J[0-9]{7}:\s+Job\sname:\s+\n    (.*?)                   # Capture the Job Name\n    \s+J[0-9]{7}:\s+        # Match and ignore the last part\n/x';\n\npreg_match(\n    $pattern,\n    $this->getResultVar('FullMessage'),\n    $atmp\n);