<?php\n// Basic Authentication Example\n$valid_users = array(\n    "betaUser1" => "password123",\n    "betaUser2" => "password456"\n);\n\nif (!isset($_SERVER['PHP_AUTH_USER']) || !isset($valid_users[$_SERVER['PHP_AUTH_USER']]) || $valid_users[$_SERVER['PHP_AUTH_USER']] !== $_SERVER['PHP_AUTH_PW']) {\n    header('WWW-Authenticate: Basic realm="Beta Testing Site"');\n    header('HTTP/1.0 401 Unauthorized');\n    echo 'Unauthorized';\n    exit;\n}\n?>