if (isset($_POST['remember_me'])) {\n    $token = bin2hex(random_bytes(16)); // Generate a secure token\n    $userId = $user['id']; // Assuming you have the user's ID after login\n    $expiry = time() + (86400 * 30); // 30 days from now\n\n    // Store the token in the database\n    $stmt = $pdo->prepare("INSERT INTO user_tokens (user_id, token, expiry) VALUES (?, ?, ?)");\n    $stmt->execute([$userId, $token, $expiry]);\n\n    // Set the cookie\n    setcookie('remember_me', $token, $expiry, "/", "", false, true);\n}