<?php\n/* Database connection */\ninclude('configuration.php');\n\nif (isset($_GET['id'])) {\n    $ID = intval($_GET['id']); // Ensure the ID is an integer\n    $sql = "DELETE FROM student WHERE id=?";\n    \n    if ($stmt = $conn->prepare($sql)) {\n        $stmt->bind_param("i", $ID);\n        $stmt->execute();\n        $stmt->close();\n    }\n    \n    echo "Record deleted";\n} else {\n    echo "0";\n}\n?>