function replaceKeysWithNames($array, $mapping) {\n    $newArray = [];\n    \n    foreach ($array as $key => $value) {\n        if (isset($mapping[$key])) {\n            $newArray[$mapping[$key]] = $value;\n        } else {\n            $newArray[$key] = $value; // retain original key if no mapping exists\n        }\n    }\n    \n    return $newArray;\n}\n\n// Example usage\n$originalArray = [\n    1 => 10,\n    2 => 20,\n    3 => 30,\n];\n\n$mappingArray = [\n    1 => 'One',\n    2 => 'Two',\n    3 => 'Three',\n];\n\n$resultArray = replaceKeysWithNames($originalArray, $mappingArray);\n\nprint_r($resultArray);