<?php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$sections_query = $mysqli->query('SELECT * FROM sections') or die($mysqli->error);\n\nwhile($row_sections = $sections_query->fetch_assoc()) {\n    echo "<h3>" . $row_sections['section_name'] . "</h3>";\n    echo "<ul>";\n\n    $section_id = $row_sections['id'];\n    $awards_sql = $mysqli->query("SELECT * FROM categories WHERE section_id = $section_id") or die($mysqli->error);\n\n    while($categories = $awards_sql->fetch_assoc()) {\n        echo "<li><strong>" . $categories['category_name'] . "</strong></li>";\n    }\n\n    echo "</ul>";\n}\n\n$mysqli->close();\n?>