<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');\n\n   class MY_Log extends CI_Log {\n\n       public function __construct() {\n           parent::__construct();\n       }\n\n       public function write_log($level, $msg) {\n           $result = parent::write_log($level, $msg);\n\n           // Set the error levels you want to be notified about\n           if ($result && in_array(strtoupper($level), ['ERROR', 'WARNING'])) {\n               $this->send_email($level, $msg);\n           }\n\n           return $result;\n       }\n\n       private function send_email($level, $msg) {\n           $CI =& get_instance();\n           $CI->load->library('email');\n\n           $CI->email->from('your_email@example.com', 'Your Name');\n           $CI->email->to('recipient@example.com');\n           $CI->email->subject('CodeIgniter Error Notification');\n           $CI->email->message("An error of level $level occurred: \n\n$msg");\n\n           $CI->email->send();\n       }\n   }