<?php\n$xmlString = <<<XML\n<root>\n    <set uniquename="value1">\n        <items>\n            <item>\n                <units>\n                    ...\n                </units>\n            </item>\n            <item>\n                <units>\n                    ...\n                    <url>http://www.something</url>\n                    ...\n                </units>\n            </item>\n        </items>\n    </set>\n    <set uniquename="value2">\n        <items>\n            <item>\n                <units>\n                    ...\n                </units>\n            </item>\n            <item>\n                <units>\n                    ...\n                    <url>http://www.something2</url>\n                    ...\n                </units>\n            </item>\n        </items>\n    </set>\n</root>\nXML;\n\n$xml = simplexml_load_string($xmlString);\n\n$uniquename = "value2";\n$url = null;\n\nforeach ($xml->set as $set) {\n    if ((string)$set['uniquename'] === $uniquename) {\n        foreach ($set->items->item as $item) {\n            if (isset($item->units->url)) {\n                $url = (string)$item->units->url;\n                break 2; // Exit both loops\n            }\n        }\n    }\n}\n\nif ($url) {\n    echo "URL found: " . $url;\n} else {\n    echo "URL not found.";\n}\n?>