<?php\n// Ensure the Imagick extension is installed and enabled\nif (!extension_loaded('imagick')) {\n    die('Imagick extension is not installed');\n}\n\nfunction resizeImage($inputPath, $outputPath, $newWidth, $newHeight) {\n    try {\n        // Create an Imagick object\n        $image = new Imagick($inputPath);\n\n        // Resize the image using bicubic interpolation\n        $image->resizeImage($newWidth, $newHeight, Imagick::FILTER_CUBIC, 1);\n\n        // Optionally apply an unsharp mask to enhance the image quality\n        $image->unsharpMaskImage(0, 0.5, 1, 0.05);\n\n        // Save the resized image to the output path\n        $image->writeImage($outputPath);\n\n        // Clear resources\n        $image->clear();\n        $image->destroy();\n\n        echo "Image resized successfully!";\n    } catch (Exception $e) {\n        echo 'Error: ' . $e->getMessage();\n    }\n}\n\n// Example usage\n$inputPath = 'path/to/your/input/image.jpg';\n$outputPath = 'path/to/your/output/image.jpg';\n$newWidth = 800;\n$newHeight = 600;\n\nresizeImage($inputPath, $outputPath, $newWidth, $newHeight);\n?>